package HelloWorld;

public class AssignmentOperator {

	@SuppressWarnings("unused")
	public static void main(String[] args) {
		// TODO Auto-generated method stub
			// 6 kinds of Assignment operator
			//=  += -+ *= /= %=
		int x = 3;
		int y = 2;
		x+=y;
		System.out.println("X = "+x); 
		
		x-=y;
		System.out.println("X = "+x);
		
		
		x*=y;
		System.out.println("X = "+x);
		
		
		x/=y;
		System.out.println("X = "+x);
		
		
		x%=y;
		System.out.println("X = "+x);
		
	
		
	}

}
