package HelloWorld;

public class UnaryIncrementDecrementOperator {

	@SuppressWarnings("unused")
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Unary Operator
		// + unary plus
		// - unary minus
		// ++ Increment Operator
		// -- Decrement Operator
		
		// Example
		int x=10,result;
		result = +x;
		System.out.println("Unary + is : "+result);
		
		result = -x;
		System.out.println("Unary + is : "+result);
		
		result = ++x;
		System.out.println("Unary + is : "+result);
		
		result = x++;
		System.out.println("Unary + is : "+result);
		
		result = x;
		System.out.println("Unary + is : "+result);
		
		
		

	}

}
